<script>
//获取用户详情
import { user } from "../../../network/user"

//搜索歌曲
import { cloudSearch } from '../../../network/searchContent'

export default {
  name: "top",
  data() {
    return {
      userMassage: {},
      vipShows: false,
      searchContent: ''
    };
  },
  components:{
  },
  mounted() {
    // console.log(this.$store.state.uid);
    user(this.$store.state.uid).then((res) => {
      //   console.log(res);
      this.userMassage = res.data.profile;
    });
  },
  methods: {
    Search(){
      // console.log(123);
      // if(this.searchContent == ''){
      //   this.$toast.Toast("您还没输入内容哦",3000)
      // }else{
      //   console.log(123);
      // }
      window.document.addEventListener("keyup",(e) => {
        // console.log(e.keyCode);
        if(e.keyCode == 13 && this.searchContent != ''){
          // console.log(123);
          cloudSearch(this.searchContent).then(res => {
            // console.log(res);
            // this.$store.commit('MusicLists',res.data.result.songs)
            this.$bus.$emit('MusicSearchLists',res.data.result.songs)
            this.$router.push('/SearchMusic')
          })
        }
      })
    },
    vipShow() {
        this.vipShows = !this.vipShows
    },
    navGo(){
    // console.log(123);
    // console.log(this.$route)
    this.$router.go(-1)
  }
  },
  
};
</script>
